<include file="Public:header"/>        <style type="text/css">            .green {                color:green;            }            .red {                color:red;            }        </style>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Bank/index')}" class="on">银行列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Bank/add')}','添加银行',520,150,true,false,false,addbtn,'add',true);">添加银行</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>编号</th>								<th>银行名称</th>								<th>状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($banks)">								<volist name="banks" id="bank">									<tr>										<td>{pigcms{$bank.bank_id}</td>										<td>{pigcms{$bank.name}</td>										<td><if condition="$bank['status']"><span class="green">启用</span><else/><span class="red">关闭</span></if></td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Bank/edit',array('id'=>$bank['bank_id'],'frame_show'=>true))}','查看详细信息',520,150,true,false,false,false,'add',true);">查看</a> | <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Bank/edit',array('id'=>$bank['bank_id']))}','编辑银行信息',520,250,true,false,false,editbtn,'add',true);">编辑</a> | <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$bank.bank_id}" url="{pigcms{:U('Bank/del')}">删除</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="8">{pigcms{$page}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="8">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>