<include file="Public:header"/>        <style type="text/css">            .c-gray {                color: #999;            }            .table-list tfoot tr {                height: 40px;            }            .green {                color: green;            }            a, a:hover{                text-decoration: none;            }        </style>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Product/group')}" class="on">商品分组</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('Product/group')}" method="get">							<input type="hidden" name="c" value="Product"/>							<input type="hidden" name="a" value="group"/>							筛选:                            <input type="text" name="keyword" class="input-text" value="{pigcms{$Think.get.keyword}" />							<select name="type">								<option value="group" <if condition="$Think.get.type eq 'group'">selected</if>>商品分组</option>                                <option value="store" <if condition="$Think.get.type eq 'store'">selected</if>>店铺名称</option>							</select>                            <input type="submit" value="查询" class="button"/>						</form>					</td>				</tr>			</table>            <div class="table-list">                <table width="100%" cellspacing="0">                    <thead>                        <tr>                            <th>编号</th>                            <th>分组名称</th>                            <th>店铺名称</th>                            <th>添加时间</th>                        </tr>                    </thead>                    <tbody>                        <if condition="is_array($groups)">                            <volist name="groups" id="group">                                <tr>                                    <td>{pigcms{$group.group_id}</td>                                    <td>{pigcms{$group.group_name}</td>                                    <td>{pigcms{$group.store}</td>                                    <td>{pigcms{$group.add_time|date='Y-m-d H:i:s',###}</td>                                </tr>                            </volist>                        </if>                    </tbody>                    <tfoot>                        <if condition="is_array($groups)">                        <tr>                            <td class="textcenter pagebar" colspan="7">{pigcms{$page}</td>                        </tr>                        <else/>                        <tr><td class="textcenter red" colspan="7">列表为空！</td></tr>                        </if>                    </tfoot>                </table>            </div>		</div><include file="Public:footer"/>